import { createSlice } from '@reduxjs/toolkit';
export const adminSlice= createSlice({
  name: 'admin',
  initialState: {
    allproducts:[],
    allusers:[],
    alldrivers:[],
    allcategories:[]
  },
  reducers: {
    loadallusers: (state,action) => {
      state.allusers=[],
      state.allusers.push(action.payload);
      
    },
    loadallproducts: (state,action) => {
      state.allproducts=[],
      state.allproducts.push(action.payload);
    },
    loadalldrivers: (state,action) => {
      state.alldrivers=[],
      state.alldrivers.push(action.payload);
    },
    loadallcategories: (state,action) => {
      state.allcategories=[],
      state.allcategories.push(action.payload);
    },
  },
});
export function getallusers(){
  return(dispatch)=>{
    fetch('http://localhost:8000/allusers')
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallusers(data[0])))
  }



}
