import { createSlice } from '@reduxjs/toolkit';
export const authenticSlice= createSlice({
  name: 'authentication',
  initialState: {
    static: 0,
  },
  reducers: {
    increment: (state) => {
      state.count = state.count + 1;
    },
    decrement: (state) => {
      state.count = state.count - 1;
    },
    reset: (state) => {
      state.count = 0;
    },
  },
});
export const { increment, decrement, reset } = authenticSlice.actions;
export default authenticSlice.reducer;
