import './Register.css'
import React,{useRef,useEffect,useState}from 'react'
import { Link } from 'react-router-dom';
import {useFormik} from 'formik'


export const Register = () => {
    var usernameref=useRef();
    var emailref=useRef();
    var phonenumberref=useRef();
    var passwordref=useRef();
    var confirmpasswordref=useRef();

    const formik=useFormik({
        initialValues:{
            username:'',
            email:'',
            phonenumber:'',
            password:'',
            confirmpassword:''

        },
        onSubmit:(values,e)=>{
            e.prevntDefault()
            console.log(values)

        },
        validate:(values)=>{

        }
    })

    function handleuname(e){
        if(e.key==='Enter'){
            emailref.current.focus()
        }
        
    }
    function handleemail(e){
        if(e.key==='Enter'){
            phonenumberref.current.focus()
        }
        
    }
    function handlepnumber(e){
        if(e.key==='Enter'){
            passwordref.current.focus()
        }
        
    }
    function handlepassword(e){
        if(e.key==='Enter'){
            confirmpasswordref.current.focus()
        }
        
    }
    useEffect(()=>{
        usernameref.current.focus()

    })

  return (
     
    <div className="pt-5">  
            <div className="global-container  main">  
                <div className="card login-form car">  
                    <div className="card-body">  
                        <h3 className="card-title text-center">  Register  </h3>  
                        <div className="card-text">  
                            <form onSubmit={formik.handleSubmit} >  
                                  
                                    
                                    <input type="text" ref={usernameref} onKeyUp={handleuname} placeholder="Username"className="form-control form-control-sm mt-3"   autoComplete='new-password' required  {...formik.getFieldProps('username')} />  
                                
                                
                                     
                                    <input type="email" ref={emailref} onKeyUp={handleemail} placeholder="Email Id" className="form-control form-control-sm mt-3"   autoComplete='new-password' required {...formik.getFieldProps('email')}/>  
                                
                                     
                                    <input type="number" ref={phonenumberref} onKeyUp={handlepnumber} placeholder="phone Numer" className="form-control form-control-sm mt-3" autoComplete='new-password' required  {...formik.getFieldProps('phonenumber')} />  
                                
                                    <input type="password"  ref={passwordref} placeholder="Password" onKeyUp={handlepassword} className="form-control form-control-sm mt-3"   required onChange={formik.handleChange} {...formik.getFieldProps('password')}/>  
                                  
                                    
                                    <input type="password"  ref={confirmpasswordref}  placeholder="Confirm  Password" className="form-control form-control-sm mt-3"   required  onChange={formik.handleChange} {...formik.getFieldProps('confirmpassword')}/>  
                               
                                  
                                <button type="submit" className="btn btn-primary btn-block"> Register </button>  
                                
                                <div className="sign-up">  
                                    Already have an account? <Link to='/'>Login</Link>  
                                </div>  
                            </form>  
                        </div>  
                    </div>  
            </div>  
    </div>
</div>

  )
}
