import './Login.css'
import React,{useRef,useEffect}from 'react'
import {Link} from 'react-router-dom'
import { useFormik } from 'formik'
import * as yup from 'yup'
import YupPassword from 'yup-password'
YupPassword(yup);


export const Login = () => {


    const validations=yup.object().shape({
       
        
        password:yup.string().required('Password is required').min(8,'Minimum 8 characters').max(30,'limit exceded'),
        email:yup.string().required('email required').email('Inavlid email format')        

    })

var formik=useFormik({
    initialValues:{
        email:'',
        password:''
    },
    validationSchema:validations,
    onSubmit:(values)=>{
        console.log(values)

    }


})

    var emailref=useRef();
    var passwordref=useRef();

    
function handleemail(e){
 if(e.key==='Enter'){
     passwordref.current.focus();
 }
}

   useEffect(()=>{
       emailref.current.focus();

   }) 
  return (
     
    <div className="pt-5">  
            <div className="global-container  main">  
                <div className="card login-form car">  
                    <div className="card-body">  
                        <h3 className="card-title text-center">  Login  </h3>  
                        <div className="card-text">  
                            <form onSubmit={formik.handleSubmit} className="form-control m-1">  
                                
                                
                                    <input type="email"  placeholder="Email Id" ref={emailref}  className="form-control form-control-sm mt-3"   autoComplete='new-password' required {...formik.getFieldProps('email')}/> 
                                    {formik.touched.email&&formik.errors.email?<div className='errors'>{formik.errors.email}</div>:null} 
                                    
                                     
                                    <input type="password"  placeholder="Password" ref={passwordref} className="form-control form-control-sm mt-3"   required {...formik.getFieldProps('password')}/>
                                    {formik.touched.password&&formik.errors.password?<div className='errors'>{formik.errors.password}</div>:null}
                                <button type="submit" className="btn btn-primary btn-block"> Login </button>  
                                
                                <div className="sign-up">  
                                    Don't have an account? <Link to='/register'> Register</Link>  
                                </div>  
                            </form>  
                        </div>  
                    </div>  
            </div>  
    </div>
</div>

  )
}
