import React,{useEffect,useState,useRef} from 'react';
import {Container ,Card,Row, Col, Button} from 'react-bootstrap';
import {useDispatch, useSelector} from 'react-redux'; 
import { useFormik } from 'formik';
import * as yup from 'yup';
 import { getalldrivers,getallusers,getallproducts,getallcategories } from '../../store/adminSlice';
const Dashboard = () => {
   const dispatch=useDispatch();
   const allproducts=useSelector((state)=>state.admin.allproducts)
   const allusers=useSelector((state)=>state.admin.allusers)
   const allcategories=useSelector((state)=>state.admin.allcategories)
   const alldrivers=useSelector((state)=>state.admin.alldrivers)
   
   var uref=useRef();
   var eref=useRef();
   var pnref=useRef();
   var pref=useRef();
   var cpref=useRef();


   const[dummyregisters,setdummyregisters] = useState([])
   const[dummycourseregisters,setdummycourseregisters] = useState([])


useEffect(()=>{
    dispatch(getallproducts());
    dispatch(getallusers());
    dispatch(getallcategories());
    dispatch(getalldrivers());

},[dispatch])

    var [s,sets]=React.useState(0)
   function handleallproducts(){
       sets(0)
   }
    function handleaddproduct(){
       
        sets(1)
        
        
    }
    function handleallcategories(){
        
        sets(2)
        
        
    }
    function handleaddcategory(){
        
        sets(3)
        
    }
    function handlealldrivers(){
        
        sets(4)
        
    }
    function handleadddriver(){
        
        sets(5)
        
    }
    function handleallorders(){
        
        sets(6)
        
    }
    function handleallusers(){
        
        sets(7)
        
    }

    const validations=yup.object().shape({
        username:yup.string().required('Username is required').min(3,'too short..').max(35,'too long...'),
        phonenumber:yup.string().required('phone number rrequired').min(10,'Not valid phone number').max(10,'Not valid phone number').matches(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/,'Not a valid phonne number'),
        password:yup.string().required('Password is required').min(8,'password must contain 8 or more characters with at least one of each: uppercase, lowercase, number and special').max(30,'Maximum 30 charracters are allowed')
                    .minLowercase(1,'password must contain at least 1 lower case letter').minUppercase(1,'password must contain at least 1 upper case letter').minNumbers(1, 'password must contain at least 1 number')
                    .minSymbols(1, 'password must contain at least 1 special character'),
        confirmpassword:yup.string().required(' confirm Password is required').oneOf([yup.ref('password'),null],'password and confirm passwords should match..'),
        email:yup.string().required('email is required').email('Inavlid email format')    

    })

    const formik=useFormik({
        initialValues:{
            username:'',
            email:'',
            phonenumber:'',
            password:'',
            confirmpassword:''

        },
        validationSchema:validations,
        
        onSubmit:(values)=>{
           
            const newuser={
                name:values.username,
                email:values.email,
                phonenumber:values.phonenumber,
                password:values.password,
                role_id:3
            }
            
           
        }
        
    })
    
 
    return (
    <div className="col main pt-5 mt-3">
         
        <div className="row mb-3">
        <div className="col-xl-3 col-sm-6 py-2" onClick={handleallproducts}>
                <div className="card bg-success text-white h-100">
                    <div className="card-body bg-primary" style={{backgroundColor:"#57b960"}}>
                        
                        <h6 className="text-uppercase">All products</h6>
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleaddproduct}>
                <div className="card bg-success text-white h-100">
                    <div className="card-body bg-success" style={{backgroundColor:"#57b960"}}>
                        
                        <h6 classname="text-uppercase">ADD PRODUCT</h6>
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleallcategories}>
                <div className="card text-white bg-danger h-100">
                    <div className="card-body bg-danger">
                        
                        <h6 className="text-uppercase" >ALL CATEGORIES</h6>
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleaddcategory}>
                <div className="card text-white bg-info h-100">
                    <div className="card-body bg-info">
                        
                        <h6 className="text-uppercase">ADD CATEGORY</h6>
                        
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handlealldrivers}>
                <div className="card text-white bg-warning h-100">
                    <div className="card-body">
                        
                        <h6 className="text-uppercase">ALL DRIVERS</h6>
                        
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleadddriver}>
                <div className="card text-white bg-info h-100">
                    <div className="card-body bg-info">
                        
                        <h6 className="text-uppercase">ADD DRIVER</h6>
                        
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleallorders}>
                <div className="card text-white bg-secondy h-100">
                    <div className="card-body bg-secondary">
                        
                        <h6 className="text-uppercase"> ALL ORDERS</h6>
                        
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleallusers}>
                <div className="card text-white bg-success h-100">
                    <div className="card-body bg-success">
                        
                        <h6 className="text-uppercase">ALL USERS</h6>
                        
                    </div>
                </div>
            </div>
        </div>


        <div className={(s===0 && s!==1 && s!==2 && s!==3 && s!==4 && s!==5 &&s!==6 && s!==7)?'dis':'hide'}>
            <Container className='p-4'>  
                <Row>  
                
            { allusers&& allusers.map((user,ui) => (  
            <Card
                
                key={ui}  
                 
                style={{width:"23%"}}  
                className="m-2 car"  
            >  
                <Card.Header><b><center>{user.name}</center></b></Card.Header>  
                <Card.Body>
                <center>
                <Card.Title><b>Fees:{user.email}</b></Card.Title>  
                <Card.Text>
                Duration:{user.phonenumber}
                <br/>
                Level:{user.role_id}
                <br/>
                </Card.Text> 
                </center>   
                </Card.Body>  
            </Card>  
            ))}  
            </Row>  
            </Container>  
    </div>
    <div id="div1" className={(s==1&&s!=0)?'dis':'hide'}>
    <div className="pt-5">  
            <div className="global-container  main">  
                <div className="card login-form">  
                    <div className="card-body">  
                        <h3 className="card-title text-center">  Add Product </h3>  
                        <div className="card-text">  
                            <form onSubmit={formik.handleSubmit} className="form m-1" >  
                                  
                                    
                                    <input type="text"  placeholder="Username"className="form-control form-control-sm mt-3" autoComplete='new-password' {...formik.getFieldProps('username')} required ref={uref}/>  
                                
                                    {formik.touched.username&&formik.errors.username?<div className='errors'>{formik.errors.username}</div>:null}
                                     
                                    <input type="email"  placeholder="Email Id" className="form-control form-control-sm mt-3" autoComplete='new-password'  {...formik.getFieldProps('email')} required ref={eref}/>  
                                    {formik.touched.email&&formik.errors.email?<div className='errors'>{formik.errors.email}</div>:null}
                                    
                                     
                                    <input type="number"   placeholder="phone Numer" className="form-control form-control-sm mt-3" autoComplete='new-password'  {...formik.getFieldProps('phonenumber')} required ref={pnref}/>  
                                    {formik.touched.phonenumber&&formik.errors.phonenumber?<div className='errors'>{formik.errors.phonenumber}</div>:null}
                                    <input type="password"    placeholder="Password"  className="form-control form-control-sm mt-3"  {...formik.getFieldProps('password')} ref={pref}/>  
                                  
                                    {formik.touched.password&&formik.errors.password?<div className='errors'>{formik.errors.password}</div>:null}
                                    <input type="password"   placeholder="Confirm  Password" className="form-control form-control-sm mt-3"  {...formik.getFieldProps('confirmpassword')} required ref={cpref} />  
                                    {formik.touched.confirmpassword&&formik.errors.confirmpassword?<div className='errors'>{formik.errors.confirmpassword}</div>:null}
                                  
                                <button type="submit" className="btn btn-primary btn-block"> ADD</button>  
                                
                            </form>  
                        </div>  
                    </div>  
            </div>  
    </div>
</div> 
        
        </div>
        
    </div>
    )
}
 
export default Dashboard
