import {createSlice} from '@reduxjs/toolkit';

export const loginregSlice=createSlice({
    name:'loginreg',
    initialState:{
        users:[],
        static:0,
        name:'brahmi'

    },
    reducers:{
        loadusers:(state,action)=>{
            state.users=[]
            state.users.push(action.payload)
        }

    }
})

export function getData(){
    return(dispatch)=>{
        fetch('http://localhost:8000/allusers')
        .then(res=>{res.json()})
        .then(data=>dispatch(loadusers(data)))
    }
}

export const {loadusers}=loginregSlice.actions
export default loginregSlice.reducer
