import './Register.css'
import React,{useRef,useEffect,useState}from 'react'
import { Link } from 'react-router-dom';
import {useFormik} from 'formik'


export const Register = () => {
    var unameref=useRef();
    var emailref=useRef();
    var phonenumberref=useRef();
    var passwordref=useRef();
    var confirmpasswordref=useRef();

    const formik=useFormik({
        initialValues:{
            username:'',
            email:'',
            phonenumber:'',
            password:'',
            confirmpassword:''

        },
        onSubmit:(values,e)=>{
            e.prevntDefault()

        },
        validate:(values)=>{

        }
    })

    function handleuname(e){
        if(e.key==='Enter'){
            emailref.current.focus()
        }
        
    }
    function handleemail(e){
        if(e.key==='Enter'){
            phonenumberref.current.focus()
        }
        
    }
    function handlepnumber(e){
        if(e.key==='Enter'){
            passwordref.current.focus()
        }
        
    }
    function handlepassword(e){
        if(e.key==='Enter'){
            confirmpasswordref.current.focus()
        }
        
    }
    useEffect(()=>{
        unameref.current.focus()

    })

  return (
     
    <div className="pt-5">  
            <div className="global-container  main">  
                <div className="card login-form car">  
                    <div className="card-body">  
                        <h3 className="card-title text-center">  Register  </h3>  
                        <div className="card-text">  
                            <form onSubmit={formik.onSubmit} method="post" name="register">  
                                <div className="form-group">  
                                    
                                    <input type="text" name="username" ref={unameref} onKeyUp={handleuname} placeholder="Username"className="form-control form-control-sm"  aria-describedby="emailHelp" autoComplete='new-password' required onChange={formik.handleChange}/>  
                                </div>
                                <div className="form-group">  
                                     
                                    <input type="email" name="email" ref={emailref} onKeyUp={handleemail} placeholder="Email Id" className="form-control form-control-sm"  aria-describedby="emailHelp" autoComplete='new-password' required onChange={formik.handleChange}/>  
                                </div>
                                <div class="form-group">  
                                     
                                    <input type="number" name="phonenumber"ref={phonenumberref} onKeyUp={handlepnumber} placeholder="phone Numer" className="form-control form-control-sm" autoComplete='new-password' required   onChange={formik.handleChange}/>  
                                </div>
                                <div className="form-group">  
                                     
                                    <input type="password" name="password" ref={passwordref} placeholder="Password" onKeyUp={handlepassword} className="form-control form-control-sm"  aria-describedby="emailHelp" required   onChange={formik.handleChange}/>  
                                </div>
                                <div className="form-group">  
                                    
                                    <input type="password" name="confirmpassword" ref={confirmpasswordref}  placeholder="Confirm  Password" className="form-control form-control-sm"  aria-describedby="emailHelp" required   onChange={formik.handleChange}/>  
                                </div>  
                                  
                                <button type="submit" className="btn btn-primary btn-block"> Register </button>  
                                
                                <div className="sign-up">  
                                    Already have an account? <Link to='/login'>Login</Link>  
                                </div>  
                            </form>  
                        </div>  
                    </div>  
            </div>  
    </div>
</div>

  )
}
