import { createSlice } from '@reduxjs/toolkit';
export const userSlice= createSlice({
  name: 'user',
  initialState: {
    allproducts:[],
    allcategories:[],
    allcartitems:[],
    allorders:[],
  },
  reducers: {
    loadallcartitems: (state,action) => {
      state.allcartitems=[]
      state.allcartitems.push(action.payload);
      
      
    },
    loadallproducts: (state,action) => {
      state.allproducts=[]
      state.allproducts.push(action.payload);
    },
    loadallorders: (state,action) => {
      state.allorders=[]
      state.allorders.push(action.payload);
    },
    loadallcategories: (state,action) => {
      state.allcategories=[]
      state.allcategories.push(action.payload);
    },
  },
});
export function getallcategories(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/user/allcategories',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallcategories(data)))
    .catch((err)=>console.log(err))

  }


}
export function getallproducts(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/user/allproducts',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallproducts(data)))
    .catch((err)=>console.log(err))

  }


}
export function getallcartitems(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/userr/allcartitems',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallcartitems(data)))
    .catch((err)=>console.log(err))

  }


}
export function getallorders(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/user/allorders',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallproducts(data)))
    .catch((err)=>console.log(err))

  }


}
export const { increment, decrement, reset } = userSlice.actions;
export default userSlice.reducer;
