import { createSlice } from '@reduxjs/toolkit';
export const authenticSlice= createSlice({
  name: 'authentication',
  initialState: {
    users:[]
  },
  reducers: {
    loadusers: (state,action) => {
      state.users=[]
      state.users.push(action.payload);
    }
  },
});
export function getData(){
  return((dispatch)=>{

    fetch('http://localhost:8000/allusers')
    .then((res)=>res.json())
    .then((data)=>dispatch(loadusers(data[0])))

  })
}
export function loginuser(loguser){
  return(dispatch)=>{
    fetch('http://localhost:8000/loginauthentication',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body:JSON.stringify(loguser)
    })
    .then((res)=>{
      if(res.status===200){
        res.json()
        .then((data)=>{
          if(data.role_id===1){
            alert("Login Successful as admin")
            window.location.href='/admin'
          }
          if(data.role_id===2){
            alert("Login Successful as driverr")
            window.location.href='/driverr'
          }
          if(data.role_id===3){
            alert("Login Successful as user")
            window.location.href='/user'
          }
        })
      }
      if(res.status===405){
        alert('password did not match')
        window.location.reload()
        }
        if(res.status===410){
          alert('user not found')
          window.location.reload()
          }
        if(res.status===500){
          alert('Internal server error')
          window.location.reload()
        }
      
      
    })
    
  }
}



export function registeruser(newuser){
  return(dispatch)=>{
    fetch('http://localhost:8000/userregister',{
                method:'POST',
                headers:{
                    'Content-type':'application/json'
                },
                body:JSON.stringify(newuser)
            }).then((res)=>{
              if(res.status===200){
                dispatch(getData())
                alert("Registerd succesfully login now..")
                window.location.href='/login'
              }
              if(res.status===400){{
                alert("User existed")
                window.location.reload()
              }}
              if(res.status===500){
                alert("Internal server error")
                window.location.reload()
              }
            })
            
            
            

}}


export const { loadusers } = authenticSlice.actions;
export default authenticSlice.reducer;
