import { createSlice } from '@reduxjs/toolkit';
import { useSelector } from 'react-redux';

export const adminSlice= createSlice({
  name: 'admin',
  initialState: {
    allproducts:[],
    allusers:[],
    alldrivers:[],
    allcategories:[]
  },
  reducers: {
    loadallusers: (state,action) => {
      state.allusers=[]
      state.allusers.push(action.payload);
      
      
    },
    loadallproducts: (state,action) => {
      state.allproducts=[]
      state.allproducts.push(action.payload);
    },
    loadalldrivers: (state,action) => {
      state.alldrivers=[]
      state.alldrivers.push(action.payload);
    },
    loadallcategories: (state,action) => {
      state.allcategories=[]
      state.allcategories.push(action.payload);
    },
  },
});
export function getallusers(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/admin/allusers',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallusers(data)))
    .catch((err)=>console.log(err))

  }

}
export function getalldrivers(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/admin/alldrivers',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadalldrivers(data)))
    .catch((err)=>console.log(err))

  }


}
export function getallcategories(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/admin/allusers',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallusers(data)))
    .catch((err)=>console.log(err))

  }


}
export function getallproducts(){
  const token=localStorage.getItem('token')
  const user=localStorage.getItem('user')
  return(dispatch)=>{
  
    fetch('http://localhost:8000/admin/allusers',{
      method:'GET',
      headers:{
        'Content-Type':'application/json',
        'token':token,
        'user':user
      }
    })
    .then((res)=>res.json())
    .then((data)=>dispatch(loadallusers(data)))
    .catch((err)=>console.log(err))

  }


}




export const { loadallcategories,loadalldrivers,loadallproducts,loadallusers } = adminSlice.actions;
export default adminSlice.reducer;
