import { createSlice } from '@reduxjs/toolkit';
export const authenticSlice= createSlice({
  name: 'authentication',
  initialState: {
    users:[]
  },
  reducers: {
    loadusers: (state,action) => {
      state.users=[]
      state.users.push(action.payload);
    }
  },
});
export function getData(){
  return((dispatch)=>{

    fetch('http://localhost:8000/allusers')
    .then((res)=>res.json())
    .then((data)=>dispatch(loadusers(data[0])))

  })
}

export function registeruser(newuser){
  return(dispatch)=>{
    fetch('http://localhost:8000/userregister',{
                method:'POST',
                headers:{
                    'Content-type':'application/json'
                },
                body:JSON.stringify(newuser)
            }).then((res)=>console.log(res.status))
            
            

}}


export const { loadusers } = authenticSlice.actions;
export default authenticSlice.reducer;
