import './Login.css'
import React,{useRef,useEffect}from 'react'
import {Link} from 'react-router-dom'
import { useFormik } from 'formik'
import * as yup from 'yup'
import YupPassword from 'yup-password'
YupPassword(yup);


export const Login = () => {


    const validations=yup.object().shape({
       
        
        password:yup.string().required('Password is required').min(8,'password must contain 8 or more characters with at least one of each: uppercase, lowercase, number and special').max(30,'Maximum 30 charracters are allowed')
                    .minLowercase(1,'password must contain at least 1 lower case letter').minUppercase(1,'password must contain at least 1 upper case letter').minNumbers(1, 'password must contain at least 1 number')
                    .minSymbols(1, 'password must contain at least 1 special character'),
        email:yup.string().required('email is required').email('Inavlid email format')        

    })

var formik=useFormik({
    initialValues:{
        email:'',
        password:''
    },
    validationSchema:validations,
    onSubmit:(values)=>{

    }


})

    var emailref=useRef();
    var passwordref=useRef();

    
function handleemail(e){
 if(e.key==='Enter'){
     passwordref.current.focus();
 }
}

   useEffect(()=>{
       emailref.current.focus();

   }) 
  return (
     
    <div className="pt-5">  
            <div className="global-container  main">  
                <div className="card login-form car">  
                    <div className="card-body">  
                        <h3 className="card-title text-center">  Login  </h3>  
                        <div className="card-text">  
                            <form >  
                                
                                <div className="form-group">  
                                     
                                    <input type="email" name="email" placeholder="Email Id" ref={emailref}  onKeyUp={handleemail}className="form-control form-control-sm"  aria-describedby="emailHelp" autoComplete='new-password' required {...formik.getFieldProps('email')}/>  
                                </div>
                                
                                <div className="form-group">  
                                     
                                    <input type="password" name="password" placeholder="Password" ref={passwordref} className="form-control form-control-sm"  aria-describedby="emailHelp" required {...formik.getFieldProps('password')}/>  
                                </div>
                                
                                <button type="submit" className="btn btn-primary btn-block"> Login </button>  
                                
                                <div className="sign-up">  
                                    Don't have an account? <Link to='/register'> Register</Link>  
                                </div>  
                            </form>  
                        </div>  
                    </div>  
            </div>  
    </div>
</div>

  )
}
