import React from 'react';
import { useState ,useEffect} from 'react'; 
import {Container ,Card,Row, Col, Button} from 'react-bootstrap';
import {useSelector,useDispatch} from 'react-redux'; 
import {getallproducts,getallcategoriesget,allcartitems,getallorders} from '../store/userSlice';
 
function Dashboard(){
   const allproducts=useSelector(state=>state.user.allproducts);
   const allcategories=useSelector(state=>state.user.allcategories);
    const allorders=useSelector(state=>state.user.allorders);
    const allcartitems=useSelector(state=>state.user.allcartitems);
   
 
   useEffect(()=>{
    dispatch(getallproducts());
    dispatch(getallcategoriesget());
    dispatch(getallorders());
    dispatch(allcartitems());
   },[dispatch])

   var [s,sets]=React.useState(0)
   function handleallproducts(){
       sets(0)
   }
    function handleallcategories(){
       
        sets(1)
        
        
    }
    function handlecart(){
        
        sets(2)
        
        
    }
    function handleorders(){
        
        sets(3)
        
    }
    return (
    <div className="col main pt-5 mt-3">
         
        <div className="row mb-3">
        <div className="col-xl-3 col-sm-6 py-2" onClick={handleallproducts}>
                <div className="card bg-success text-white h-100">
                    <div className="card-body bg-primary" style={{backgroundColor:"#57b960"}}>
                        
                        <h6 className="text-uppercase">All products</h6>
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleallcategories}>
                <div className="card bg-success text-white h-100">
                    <div className="card-body bg-success" style={{backgroundColor:"#57b960"}}>
                        
                        <h6 classname="text-uppercase">ALL CATEGORIES</h6>
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handlecart}>
                <div className="card text-white bg-danger h-100">
                    <div className="card-body bg-danger">
                        
                        <h6 className="text-uppercase" >CART</h6>
                    </div>
                </div>
            </div>
            <div className="col-xl-3 col-sm-6 py-2" onClick={handleorders}>
                <div className="card text-white bg-info h-100">
                    <div className="card-body bg-info">
                        
                        <h6 className="text-uppercase">ORDERS</h6>
                        
                    </div>
                </div>
            </div>
            
            
        </div>

        
 
    </div>
    )
}
 
export default Dashboard
