import React from 'react';
import { Route} from 'react-router-dom';
 
const Guard = ({ component: Component, ...rest }) => {
 
   function hasJWT() {
       let flag = false;
 
       localStorage.getItem("token") ? flag=true : flag=false
      
       return flag
   }
 
   return (
       <Route {...rest}
           render={props => (
               hasJWT() ?
                   <Component {...props} />
                   :
                   <Redirect to={{ pathname: '/login' }} />
           )}
       />
   );
};
 
export default Guard;
